set -x addgroup -g 101 -S nginx adduser -S -D -H -u 101 -h /var/cache/nginx -s /sbin/nologin -G nginx -g nginx nginx apkArch="$(cat /etc/apk/arch)" nginxPackages=" nginx=${NGINX_VERSION}-r${PKG_RELEASE} nginx-module-xslt=${NGINX_VERSION}-r${PKG_RELEASE} nginx-module-geoip=${NGINX_VERSION}-r${PKG_RELEASE} nginx-module-image-filter=${NGINX_VERSION}-r${PKG_RELEASE} nginx-module-njs=${NGINX_VERSION}.${NJS_VERSION}-r${PKG_RELEASE} " case "$apkArch" in x86_64)     set -x               KEY_SHA512="de7031fdac1354096d3388d6f711a508328ce66c168967ee0658c294226d6e7a161ce7f2628d577d56f8b63ff6892cc576af6f7ef2a6aa2e17c62ff7b6bf0d98 *stdin"               apk add --no-cache --virtual .cert-deps openssl               wget -O /tmp/nginx_signing.rsa.pub https://nginx.org/keys/nginx_signing.rsa.pub     if [ "$(openssl rsa -pubin -in /tmp/nginx_signing.rsa.pub -text -noout | openssl sha512 -r)" = "$KEY_SHA512" ]; then         echo "key verification succeeded!"         mv /tmp/nginx_signing.rsa.pub /etc/apk/keys/     else         echo "key verification failed!"         exit 1     fi               apk del .cert-deps               apk add -X "https://nginx.org/packages/mainline/alpine/v$(egrep -o '^[0-9]+\.[0-9]+' /etc/alpine-release)/main" --no-cache $nginxPackages     ;; *)     set -x               tempDir="$(mktemp -d)"     chown nobody:nobody $tempDir               apk add --no-cache --virtual .build-deps gcc libc-dev make openssl-dev pcre-dev zlib-dev linux-headers libxslt-dev gd-dev geoip-dev perl-dev libedit-dev mercurial bash alpine-sdk findutils     su nobody -s /bin/sh -c "     export HOME=${tempDir}     cd ${tempDir}     hg clone https://hg.nginx.org/pkg-oss     cd pkg-oss     hg up ${NGINX_VERSION}-${PKG_RELEASE}     cd alpine     make all     apk index -o ${tempDir}/packages/alpine/${apkArch}/APKINDEX.tar.gz ${tempDir}/packages/alpine/${apkArch}/*.apk     abuild-sign -k ${tempDir}/.abuild/abuild-key.rsa ${tempDir}/packages/alpine/${apkArch}/APKINDEX.tar.gz     "               cp ${tempDir}/.abuild/abuild-key.rsa.pub /etc/apk/keys/     apk del .build-deps     apk add -X ${tempDir}/packages/alpine/ --no-cache $nginxPackages     ;; esac if [ -n "$tempDir" ]; then rm -rf "$tempDir"; fi if [ -n "/etc/apk/keys/abuild-key.rsa.pub" ]; then rm -f /etc/apk/keys/abuild-key.rsa.pub; fi if [ -n "/etc/apk/keys/nginx_signing.rsa.pub" ]; then rm -f /etc/apk/keys/nginx_signing.rsa.pub; fi apk add --no-cache --virtual .gettext gettext mv /usr/bin/envsubst /tmp/ runDeps="$(scanelf --needed --nobanner /tmp/envsubst | awk '{ gsub(/,/, "\nso:", $2); print "so:" $2 }' | sort -u | xargs -r apk info --installed | sort -u)" apk add --no-cache $runDeps apk del .gettext mv /tmp/envsubst /usr/local/bin/ apk add --no-cache tzdata ln -sf /dev/stdout /var/log/nginx/access.log ln -sf /dev/stderr /var/log/nginx/error.log
